	public String getQuota() {
		
		BigDecimal num=null;
		BigDecimal denom=null;
		String perc=null;
		
		if (!scQuotaDenominatore.equals("-") && !scQuotaDenominatore.equals("") && !scQuotaNumeratore.equals("-") && !scQuotaNumeratore.equals(""))
		{
			num=new BigDecimal(scQuotaNumeratore);
			denom=new BigDecimal(scQuotaDenominatore);
			perc=num.divide(denom.multiply(new BigDecimal(10)),3,1).toString();
			//return scQuotaNumeratore+"/"+scQuotaDenominatore;
			if(perc.indexOf(".000")>0)
				return perc.substring(0,perc.indexOf("."))+" %";
				else return perc+" %";
		}
		if (!sfQuotaDenominatore.equals("-") &&! sfQuotaDenominatore.equals("") && !sfQuotaNumeratore.equals("-") &&! sfQuotaNumeratore.equals(""))
		{
			num=new BigDecimal(sfQuotaNumeratore);
			denom=new BigDecimal(sfQuotaDenominatore);
			perc=num.divide(denom.multiply(new BigDecimal(10)),3,1).toString();
			if(perc.indexOf(".000")>0)
			return perc.substring(0,perc.indexOf("."))+" %";
			else return perc+" %";
			
//			return sfQuotaNumeratore+"/"+sfQuotaDenominatore;	
		}		
		return "-";
	}
	
